import { Component, Input, OnDestroy, OnInit } from "@angular/core";
import { CommonModule } from "@angular/common";
import { Subscription } from "rxjs";
import { ClickOutsideDirective } from "../../directives/click-outside.directive";
import { ThemeService } from "../../services/theme.service";
import { ThemeOption, Mode, themeOptions } from "./theme-options";

@Component({
  selector: "app-theme-switcher",
  templateUrl: "./theme-switcher.component.html",
  styleUrls: ["./theme-switcher.component.scss"],
  standalone: true,
  imports: [CommonModule, ClickOutsideDirective]
})
export class ThemeSwitcherComponent implements OnInit, OnDestroy {
  public isMenuVisible = false;
  public selectedOption!: ThemeOption;
  public themeOptions!: ThemeOption[];
  @Input() togglerLabel!: string;
  private subscription!: Subscription;

  constructor(private themeService: ThemeService) { }

  ngOnInit(): void {
    this.themeOptions = themeOptions;
    this.subscription = this.themeService.currentValue.subscribe((mode: Mode) => {
      this.selectedOption = this.getThemeOption(mode);
    })
  }

  ngOnDestroy(): void {
    if (this.subscription) {
      this.subscription.unsubscribe();
    }
  }

  public toggleMenu(): void {
    this.isMenuVisible = !this.isMenuVisible;
  }

  public updateTheme(name: Mode): void {
    this.selectedOption = this.getThemeOption(name);
    this.themeService.saveThemePreference(name);
    this.toggleMenu();
  }

  private getThemeOption(name: Mode): ThemeOption {
    return themeOptions.filter(value => value.name == name)[0];
  }

  public onClickOutside(): void {
    this.isMenuVisible = false;
  }

}
<div class="menu-container" (clickOutside)="onClickOutside()">
  <button class="menu-toggler" (click)="toggleMenu()">
    <svg height="1em" viewBox="0 0 512 512">
      <path [attr.d]="selectedOption.icon"></path>
    </svg>
    <span class="menu-toggler-text">{{togglerLabel}}</span>
  </button>
  <div class="menu" *ngIf="isMenuVisible">
    <ul>
      <li *ngFor="let val of themeOptions">
        <button class="menu-item" (click)="updateTheme(val.name)" [ngClass]="{'active':selectedOption.name === val.name}">
          <svg height="1em" viewBox="0 0 512 512">
            <path [attr.d]="val.icon"></path>
          </svg>
          {{val.name | titlecase }}
        </button>
      </li>
    </ul>
  </div>
</div>
