import { Component, Output, EventEmitter } from '@angular/core';
import { NavItem, navItems } from '../../constants/header-nav';
import { SIDENAV_DEPS } from './sidenav.dependencies';

@Component({
  selector: 'app-sidenav',
  templateUrl: './sidenav.component.html',
  styleUrls: ['./sidenav.component.scss'],
  standalone: true,
  imports: [SIDENAV_DEPS],
})
export class SidenavComponent {
  @Output() public onClose = new EventEmitter<void>();

  public sidenavItems: NavItem[] = navItems;

  public close(): void {
    this.onClose.emit();
  }
}
<div class="sidenav">
  <div class="sidenav-header">
    <h4 class="sidenav-title">Menu</h4>
    <svg class="sidenav-close" xmlns="http://www.w3.org/2000/svg" height="1.25em" viewBox="0 0 384 512"
      (click)="close()" fill="currentColor">
      <path fill="currentColor"
        d="M376.6 84.5c11.3-13.6 9.5-33.8-4.1-45.1s-33.8-9.5-45.1 4.1L192 206 56.6 43.5C45.3 29.9 25.1 28.1 11.5 39.4S-3.9 70.9 7.4 84.5L150.3 256 7.4 427.5c-11.3 13.6-9.5 33.8 4.1 45.1s33.8 9.5 45.1-4.1L192 306 327.4 468.5c11.3 13.6 31.5 15.4 45.1 4.1s15.4-31.5 4.1-45.1L233.7 256 376.6 84.5z" />
    </svg>
  </div>
  <div class="sidenav-body">
    <ul class="sidenav-list">
      <li class="sidenav-item" *ngFor="let item of sidenavItems" (click)="close()">
        <a routerLink="/" [fragment]="item.link" [routerLinkActive]="item.activeClassName" [href]="item.link"
          [className]="item.className"
          [routerLinkActiveOptions]="{ matrixParams: 'exact', queryParams: 'exact', paths: 'exact', fragment: 'exact'}">
          {{ item.title }}
        </a>
      </li>
    </ul>
    <app-theme-switcher togglerLabel="Toggle Theme"></app-theme-switcher>
  </div>
</div>
