import { Component } from "@angular/core";

@Component({
  selector: "app-footer",
  templateUrl: "./footer.component.html",
  styleUrls: ["./footer.component.scss"],
  standalone: true
})
export class FooterComponent {
  currentYear: number = new Date().getFullYear();
}
<footer class="footer">
  <div class="container">
    <p class="coopyright">
      &copy; <span id="year">{{currentYear}}</span> - Template by <a href="https://github.com/PERCY552"
        target="_blank" rel="noreferrer">MP
        RAKGOALE</a>
    </p>
  </div>
</footer>
